import React, { useState } from 'react';
import './index.css';
import ReactMarkdown from 'react-markdown';


function App() {
  const [input, setInput] = useState('');
  const [chatHistory, setChatHistory] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const res = await fetch(process.env.REACT_APP_API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: input })
      });

      const data = await res.json();
      const message = data.choices[0].message.content;

      setChatHistory(prev => [...prev, { question: input, answer: message }]);
      setInput('');
    } catch (error) {
      console.error('Error fetching chatbot response:', error);
      setChatHistory(prev => [...prev, { question: input, answer: 'Error fetching response.' }]);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>GraceChat.AI</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask a question..."
          />
          <button type="submit">Send</button>
        </form>
        <p style={{ marginTop: '1rem' }}>
          I'm here to answer questions about the Christian religion and the Bible. If you have any questions related to those topics, feel free to ask!
        </p>

        <div className="chat-container">
        {chatHistory.map((entry, index) => (
          <div key={index} className="chat-entry">
            <div className="user-question"><strong>You:</strong> {entry.question}</div>
            <ReactMarkdown className="response-box">
              {entry.answer}
            </ReactMarkdown>
          </div>
        ))}
        </div>
      </header>
    </div>
  );
}

export default App;
